int inSubString(char * str, char ch)
{
    while( *str != '\0'){
        if (ch == *str){
            return 1;
        }

        str++;
    }

    return 0;
}

int maxLenStr(char *str[], int len)
{
    int i = 0;
    int maxLen = 0;
    char *maxStr;
    char maxPos = 0;


    /*
    for(;i<len;){
        char * ptr = str[i++];
        printf("%s\n", ptr);
    }

    return 0;
    */

    while(i < len){
        char *cur = str[i];
        if (strlen(cur) > maxLen){
            maxLen = strlen(cur);
            maxPos = i;
            //strcpy(maxStr, cur);
        }

        i++;
    }

    if (len == 0){
        maxPos = 0;
    }

    maxStr = str[maxPos];

    return strlen(maxStr);
}

