char * addString(char * str, char ch)
{
    int len = strlen(str);
    char *str2 = malloc(sizeof(char)*len+1);
    if (str2 == NULL){
        exit(1);
    }

    char *tmp = malloc(sizeof(char));
    sprintf(tmp, "%c", ch);

    strcpy(str2, str);
    strcat(str2, tmp);
    free(tmp);

    return str2;
}

char * resetCurLongest(char * str, char ch)
{
    str[0] = ch;
    str[1] = '\0';

    return str;
}

